import React, { Component } from "react";
import ToolBar from "./Components/Toolbar/index";
import SideDrawer from "./Components/SideDrawer/index";
import Backdrop from "./Components/SideDrawer/Backdrop/index";
import DrawerToggleButton from "./Components/SideDrawer/DrawerToggleButton";

import "./index.scss";

class App extends Component {
  state = {
    sideDrawerOpen: false
  };

  toggleDrawerHandler = () => {
    this.setState((prevState) => {
      return {
        sideDrawerOpen: !prevState.sideDrawerOpen
      };
    });
  };
  backDrawerHandler = () => {
    this.setState({ sideDrawerOpen: false });
  };

  render() {
    return (
      <div>
      <div style={{ height: "100%" }} className="web-view">
        <ToolBar onClickHandler={this.toggleDrawerHandler} />
        {this.state.sideDrawerOpen && (
          <SideDrawer show={this.state.sideDrawerOpen} />
        )}
        {this.state.sideDrawerOpen && (
          <Backdrop onbackDrawerHandler={this.backDrawerHandler} />
        )}
      </div>
      <div className="toolbar-toggle-button">
          <DrawerToggleButton click={props.onClickHandler} />
        </div>
      </div>
    );
  }
}

export default App;
