import { useState } from "react";
import { Card, Form, Row, Col, Alert } from "react-bootstrap";

//component
import ToggleButton from "../../components/ToggleButton/index";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faBookmark,
    faLayerGroup,
    faUsers,
    faPrint,
    faMoneyBillAlt,
    faTimes,
} from "@fortawesome/free-solid-svg-icons";
import { faWhatsapp } from "@fortawesome/free-brands-svg-icons"
import Button from "../../components/Button/index";
import DashboardCard from "../../components/DashboardCard/index";
import Chart from "react-google-charts";

//scss
import "./index.scss";

export default function InwardPayment() {


    return (
        <div className="screen-stocks">
            <Alert key="infoAlert" variant="info">
                <div className="d-flex justify-content-between">
                    <div>Update Contact Phone! Please update your contact phone number</div>
                    <Button
                        name="Update Phone"
                        havingIcon={false}
                        buttonType="btn btn-info"
                    />
                </div>
            </Alert>

            <Row>
                <Col sm={12} md={6}>
                    <DashboardCard
                        name="Account Summary"
                        havingIcon={true}
                        iconType={faBookmark}
                        havingCloseIcon={false}
                        productIcon1={faLayerGroup}
                        count1={2}
                        description1="Total Products"
                        productIcon2={faUsers}
                        count2={3}
                        description2="Total Customer"
                    />
                </Col>
                <Col>
                    <DashboardCard
                        name="Total Invoice - Feb 2021"
                        havingIcon={true}
                        iconType={faBookmark}
                        havingCloseIcon={true}
                        productIcon1={faPrint}
                        count1={2}
                        description1="Total Sales Invoice"
                        productIcon2={faPrint}
                        count2={3}
                        description2="Total Purchase Invoice"
                    />
                </Col>
            </Row>

            <Row>
                <Col>
                    <DashboardCard
                        name="Total Invoice - Feb 2021"
                        havingIcon={true}
                        iconType={faBookmark}
                        havingCloseIcon={true}
                        productIcon1={faMoneyBillAlt}
                        count1={2000}
                        subCount1="+ GST 96"
                        description1="Total Sales Amount"
                        productIcon2={faMoneyBillAlt}
                        count2={300}
                        subCount2="+ GST 96"
                        description2="Total Purchase Amount"
                    />
                </Col>
            </Row>

            <Row>
                <Col>
                    <DashboardCard
                        name="Total Payment Outstanding"
                        havingIcon={true}
                        iconType={faBookmark}
                        havingCloseIcon={true}
                        productIcon1={faMoneyBillAlt}
                        count1={0}
                        description1="Total Sales Outstanding"
                        productIcon2={faMoneyBillAlt}
                        count2={300}
                        description2="Total Purchase Outstanding"
                    />
                </Col>
            </Row>

            <Row>
                <Col sm={12} md={6}>
                    <Card className="card-style mb-4">
                        <Card.Text className="card-title pb-2 d-flex justify-content-between m-0">
                            <div>
                                <FontAwesomeIcon icon={faBookmark} className="mr-2 mt-3" />
            Invoice Count Summary
          </div>
                            <FontAwesomeIcon icon={faTimes} className="mr-2 mt-3" />
                        </Card.Text>
                        <Card.Text>
                            <Chart
                                width={"100%"}
                                height={"350px"}
                                chartType="Bar"
                                loader={<div>Loading Chart</div>}
                                data={[
                                    ["Year", "Sales", "Expenses"],
                                    ["2014", 1000, 1000],
                                    ["2015", 1170, 460],
                                    ["2016", 660, 1120],
                                    ["2017", 1030, 540]
                                ]}
                                options={{
                                    colors: ['#ededed', '#cadee7'],
                                    // Material design options
                                    chart: {
                                        title: "Company Performance",
                                        subtitle: "Sales, Expenses, and Profit: 2014-2017"
                                    }
                                }}
                                // For tests
                                rootProps={{ "data-testid": "2" }}
                            />
                        </Card.Text>
                    </Card>

                </Col>
                <Col sm={12} md={6}>
                    <Card className="card-style mb-4">
                        <Card.Text className="card-title pb-2 d-flex justify-content-between m-0">
                            <div>
                                <FontAwesomeIcon icon={faBookmark} className="mr-2 mt-3" />
            Invoice Amount Summary
          </div>
                            <FontAwesomeIcon icon={faTimes} className="mr-2 mt-3" />
                        </Card.Text>
                        <Card.Text>
                            <Chart
                                width={'100%'}
                                height={"350px"}
                                chartType="LineChart"
                                loader={<div>Loading Chart</div>}
                                data={[
                                    ['x', 'Purchase', 'Sale'],
                                    [0, 0, 0],
                                    [1, 10, 5],
                                    [2, 23, 15],
                                    [3, 17, 9],
                                    [4, 18, 10],
                                    [5, 9, 5],
                                    [6, 11, 3],
                                    [7, 27, 19],
                                ]}
                                options={{
                                    colors: ['#ededed', '#cadee7'],
                                    series: {
                                        1: { curveType: 'function' },
                                    },
                                }}
                                rootProps={{ 'data-testid': '2' }}
                            />
                        </Card.Text>
                    </Card>
                </Col>
            </Row>

            <Row>
                <Col>
                    <Card className="card-style mb-4">
                        <Card.Text className="card-title pb-2 d-flex justify-content-between m-0">
                            <div>
                                <FontAwesomeIcon icon={faBookmark} className="mr-2 mt-3" />
                                Invoice Count Summary
                            </div>
                            <div>
                            <Button
                                    name="Vi"
                                    havingIcon={true}
                                    iconType={faPrint}
                                    buttonType="btn btn-primary"
                                />
                            <FontAwesomeIcon icon={faTimes} className="mr-2 mt-3" />
                            </div>
                        </Card.Text>
                        <Card.Text></Card.Text>
                    </Card>
                </Col>
            </Row>
        </div>
    );
}
