import { Card, Row, Col } from "react-bootstrap";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faTimes
} from "@fortawesome/free-solid-svg-icons";

//scss
import "./index.scss";

const DashboardCard = (props) => {
  const { name, havingIcon, iconType, havingCloseIcon, count, description } = props;
  return (
    <div className="button-style">
      <Card className="card-style mb-4">
                <Card.Text className="card-title pb-2 d-flex justify-content-between m-0">
                    <div>
                      {havingIcon && (
                        <FontAwesomeIcon icon={iconType} className="mr-2 mt-3" />
                        )}
                        {name}
                    </div>
                    {havingCloseIcon && (
                      <FontAwesomeIcon icon={faTimes} className="mr-2 mt-3" />
                    )}
                </Card.Text>
                <Card.Text>
                  <Row>
                    <Col sm={6} md={6}>
                      <div className="d-flex justify-content-center ">
                      <FontAwesomeIcon icon={iconType} className="mr-2 mt-3" />
                    <h2>{count}</h2>
                    <h3>{description}</h3>
                      </div>
                    </Col>
                    <Col sm={6} md={6}>
                      <div>
                      <FontAwesomeIcon icon={iconType} className="mr-2 mt-3" />
                    <h2>{count}</h2>
                    <h3>{description}</h3>
                      </div>
                    </Col>
                  </Row>
                </Card.Text>
          </Card>
    </div>
  );
};

DashboardCard.propTypes = {
  name: PropTypes.string,
  havingIcon: PropTypes.bool,
  havingCloseIcon: PropTypes.bool,
  iconType: PropTypes.string
};

DashboardCard.defaultProps = {
  havingIcon: false,
  havingCloseIcon: false
};

export default DashboardCard;
